SqlOperation.TextCommand:
SELECT * FROM `sec_secrets`
WHERE `WorkspaceId` = @WorkspaceId
ORDER BY `Name` ASC;

INSERT INTO `sec_secrets`
(`Id`, `WorkspaceId`, `Name`, `Description`, `Disabled`, `Encrypted`, `Value`)
VALUES
(@Id, @WorkspaceId, @Name, @Description, @Disabled, @Encrypted, @Value);

SELECT * FROM `sec_secrets`
WHERE `WorkspaceId` = @WorkspaceId AND `Id` = @Id;
